import React, { useState } from 'react';
import axios from 'axios';
import './TestGrading.css';

const TestGrading = () => {
  const [answers, setAnswers] = useState('');
  const [result, setResult] = useState(null);

  const handleInputChange = (event) => {
    setAnswers(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('/api/grade-test', { answers });
      setResult(response.data);
    } catch (error) {
      console.error('Error grading test:', error);
    }
  };

  return (
    <div className="test-grading">
      <h2>Test Grading</h2>
      <form onSubmit={handleSubmit}>
        <textarea 
          value={answers} 
          onChange={handleInputChange} 
          placeholder="Enter the test answers here" 
          rows="10" 
          cols="50"
        />
        <button type="submit">Grade Test</button>
      </form>
      {result && (
        <div className="result">
          <h3>Grading Result:</h3>
          <pre>{JSON.stringify(result, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default TestGrading;
