// src/TestGrading/TestGrading.js
import React, { useState } from 'react';
import Tesseract from 'tesseract.js';
import cv from '@techstark/opencv-js';

const TestGrading = () => {
  const [image, setImage] = useState(null);
  const [text, setText] = useState('');
  const [grade, setGrade] = useState(null);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setImage(URL.createObjectURL(file));
    processImage(file);
  };

  const processImage = (file) => {
    const img = new Image();
    img.src = URL.createObjectURL(file);
    img.onload = () => {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');
      canvas.width = img.width;
      canvas.height = img.height;
      ctx.drawImage(img, 0, 0);

      const src = cv.imread(canvas);
      const gray = new cv.Mat();
      cv.cvtColor(src, gray, cv.COLOR_RGBA2GRAY, 0);

      // Detect circles (assuming the answers are marked with circles)
      const circles = new cv.Mat();
      cv.HoughCircles(gray, circles, cv.HOUGH_GRADIENT, 1, gray.rows / 8, 100, 30, 1, 30);

      // Draw circles on the original image for visualization
      for (let i = 0; i < circles.cols; ++i) {
        const x = circles.data32F[i * 3];
        const y = circles.data32F[i * 3 + 1];
        const radius = circles.data32F[i * 3 + 2];
        cv.circle(src, new cv.Point(x, y), radius, new cv.Scalar(255, 0, 0, 255), 3);
      }

      cv.imshow('canvasOutput', src);

      // Extract text using Tesseract
      Tesseract.recognize(
        canvas,
        'eng',
        {
          logger: (m) => console.log(m),
        }
      )
        .then(({ data: { text } }) => {
          console.log(text);
          setText(text);
          gradeTest(text);
        })
        .catch((err) => {
          console.error(err);
        });

      // Clean up
      src.delete();
      gray.delete();
      circles.delete();
    };
  };

  const gradeTest = (extractedText) => {
    fetch('http://localhost:5000/grade-test', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ text: extractedText }),
    })
      .then((response) => response.json())
      .then((data) => setGrade(data.grade))
      .catch((error) => console.error('Error:', error));
  };

  return (
    <div>
      <h1>Test Grading</h1>
      <input type="file" accept="image/*" onChange={handleImageUpload} />
      {image && <img src={image} alt="Uploaded test" style={{ maxWidth: '100%', height: 'auto' }} />}
      <div id="canvasOutput"></div>
      <div>
        <h2>Extracted Text</h2>
        <pre>{text}</pre>
      </div>
      <div>
        <h2>Grade</h2>
        <p>{grade}</p>
      </div>
    </div>
  );
};

export default TestGrading;
