import React, { useState } from 'react';
import axios from 'axios';
import UploadRubric from './UploadRubric'; 
import './App.css';  // Importing CSS for styling

function App() {
    const [text, setText] = useState('');
    const [grade, setGrade] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/submit_assignment', { text });
            const parsedGrade = parseGrade(response.data.grade);
            setGrade(parsedGrade);
        } catch (error) {
            console.error('Error submitting assignment:', error);
            alert('Failed to submit assignment.');
        }
    };

    const parseGrade = (gradeText) => {
        const categoryPattern = /^([\w\s&]+):\s(\d)/;
        const lines = gradeText.split('\n');
        let formattedGrade = '';
        let feedback = '';
        let inFeedbackSection = false;

        lines.forEach(line => {
            const match = line.match(categoryPattern);
            if (match) {
                const category = match[1].trim();
                const score = match[2].trim();
                formattedGrade += `${category}: ${score}\n`;
                inFeedbackSection = false;
            } else {
                if (line.trim()) {
                    if (!inFeedbackSection && formattedGrade) {
                        feedback += '\n• ';
                        inFeedbackSection = true;
                    }
                    feedback += line.trim() + ' ';
                }
            }
        });

        feedback = feedback.trim().replace(/(?<!\n)(?<!•) ([A-Z])/g, '\n• $1').replace(/\n•$/, '');

        return { formattedGrade, feedback };
    };

    return (
        <div className="App">
            <h1>AI Grading System</h1>
            <UploadRubric /> {/* Including the UploadRubric component */}

            <form onSubmit={handleSubmit}>
                <textarea
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    placeholder="Write your assignment here"
                ></textarea>
                <button type="submit">Submit</button>
            </form>
            {grade && (
                <div className="grade-container">
                    <h2>Assignment Grade</h2>
                    <pre className="grade-text">{grade.formattedGrade}</pre>
                    <ul className="feedback-list">
                        {grade.feedback.split('\n').map((item, index) => (
                            <li key={index}>{item}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
}

export default App;
