import React, { useState } from 'react';
import axios from 'axios';
import LoadingBar from '../LoadingBar/LoadingBar';
import './TestGrading.css';

const TestGrading = () => {
  const [image, setImage] = useState(null);
  const [grade, setGrade] = useState(null);
  const [extractedText, setExtractedText] = useState('');
  const [markedAnswers, setMarkedAnswers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const fileInputRef = useRef(null);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setImage(URL.createObjectURL(file));
    uploadImage(file);
  };

  const uploadImage = async (file) => {
    setLoading(true);
    setProgress(0);

    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post('http://localhost:5000/grade-test', formData, {
        onUploadProgress: (progressEvent) => {
          const total = progressEvent.total;
          const current = progressEvent.loaded;
          setProgress(Math.round((current / total) * 100));
        },
      });

      // Simulate processing time to show the loading bar incrementally
      for (let i = progress; i <= 100; i += 10) {
        await new Promise((resolve) => setTimeout(resolve, 100));
        setProgress(i);
      }

      setGrade(response.data.grade);
      setExtractedText(response.data.extracted_text);
      setMarkedAnswers(response.data.marked_answers);
    } catch (error) {
      console.error('Error uploading image:', error);
      alert('Failed to upload image.');
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setImage(null);
    setGrade(null);
    setExtractedText('');
    setMarkedAnswers([]);
    setProgress(0);
    if (fileInputRef.current) {
        fileInputRef.current.value = '';
    }
  };

  return (
    <div className="test-grading">
      <h1>Test Grading</h1>
      <input type="file" accept="image/*" onChange={handleImageUpload} ref={fileInputRef}/>
      {image && <img src={image} alt="Uploaded test" style={{ maxWidth: '100%', height: 'auto' }} />}

      {loading && <LoadingBar progress={progress} />}


      {grade && (
        <div className="grade-container">
          <h2>Grade</h2>
          <div style={{ whiteSpace: 'pre-wrap', fontFamily: 'monospace' }}>{grade}</div>
        </div>
      )}
      {(grade || extractedText || markedAnswers.length > 0) && (
        <button onClick={handleReset}>Reset</button>
      )}
    </div>
  );
};

export default TestGrading;
