import React, { useState } from 'react';
import axios from 'axios';
import UploadRubric from './UploadRubric'; 
import './App.css';

function App() {
    const [text, setText] = useState('');
    const [grade, setGrade] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/submit_assignment', { text });
            setGrade(response.data.grade);
        } catch (error) {
            console.error('Error submitting assignment:', error);
            alert('Failed to submit assignment.');
        }
    };

    return (
        <div className="App">
            <h1>AI Grading System</h1>
            <UploadRubric /> {/* Including the UploadRubric component */}

            <form onSubmit={handleSubmit}>
                <textarea
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    placeholder="Write your assignment here"
                ></textarea>
                <button type="submit">Submit</button>
            </form>
            {grade && <div>Your Grade: {grade}</div>}
        </div>
    );
}

export default App;
