import React, { useState } from 'react';

const UploadRubric = () => {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('Please select a file first.');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await fetch('http://localhost:5000/upload_rubric', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      alert(`Rubric uploaded successfully! Extracted text: ${data.rubric_text}`);
    } catch (error) {
      console.error('Error uploading rubric:', error);
      alert('Failed to upload rubric.');
    }
  };

  return (
    <div>
      <h1>Upload Rubric</h1>
      <input type="file" accept="application/pdf" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload Rubric</button>
    </div>
  );
};

export default UploadRubric;
